import React from "react";

const Ninjas = (props) => {
  // console.log(props);
  const { ninjas } = props;
  const { deleteNinja } = props;
  const ninjaList = ninjas.map((ninja) => {
    if (ninja.age > 20) {
      return (
        <div className="ninja" key={ninja.id}>
          <div>Name: {ninja.name}</div>
          <div>Age: {ninja.age}</div>
          <div>Belt: {ninja.belt}</div>
          <button
            onClick={() => {
              deleteNinja(ninja.id);
            }}
          >
            Delete Ninja
          </button>
          <hr></hr>
        </div>
      );
    } else {
      return null;
    }
  });
